DROP PROCEDURE IF EXISTS `aa`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `aa`(IN `stype` int , IN `category_type` int)
BEGIN
 set @stype = stype;
 set @category_type = category_type;
 set @daysql = "SELECT count(*) count, DATE_FORMAT(launch_date_raising,'%Y-%m-%d') sdate from project p WHERE 1=1 ";
 set @hoursql = "SELECT count(*) count, DATE_FORMAT(launch_date_raising,'%H') shour from project p WHERE p.launch_date_raising >=(SELECT curdate()) and p.launch_date_raising <(SELECT date_sub(curdate(),interval +1 month))";
 set @stmt = '';
 IF(stype<>'')
		THEN
				IF(stype=1)
                THEN 
										set @stmt = @daysql;
										IF(category_type<>'')
											THEN 
													set @stmt = CONCAT(@daysql,' and p.category_id=''',@category_type,'''');
										END IF;
										set @stmt = CONCAT(@stmt,' GROUP BY sdate ORDER BY sdate');
				END IF;
				IF(stype=2)
								THEN 
										set @stmt = @hoursql;
										IF(category_type<>'')
											THEN 
												 set @stmt = CONCAT(@daysql,' and p.category_id=''',@category_type,'''');
										END IF;
									 set @stmt = CONCAT(@stmt,' GROUP BY shour ORDER BY shour');
				END IF;
END IF;


prepare stmt from @stmt;/**预编译SQL**/
EXECUTE stmt;/**执行SQL**/
	
END
;;
DELIMITER ;
