INSERT INTO `project` VALUES ('4', '“尚德红桔”的味道，初恋的味道。', '1、解决我镇桔农往年滞销的难题。2、把我们最优质，口感最纯正的“尚德红桔”带到大家面前。3、给部分贫困户家庭来来一定的收入，贴补家用。', 'img/project/shoutu/4.png', '5000', '800', '30', '2', '2016-05-05 00:00:00', '12', '0', '1', '1', '个人', null, '1');
INSERT INTO `project` VALUES ('5', '社创少年的倾听者手环，助家庭顺畅沟通！', '众筹费用用于设计制作手环套装，设计和印刷彩铅绘本，让每个家庭更好地运用手环，体会到沟通和理解的欢乐！', 'img/project/shoutu/5.jpg', '30000', '800', '30', '2', '2016-12-14 00:00:00', '25', '0', '1', '1', '个人', null, '1');
INSERT INTO `project` VALUES ('6', '给山里的孩子一个足球梦', '虽然放学后他们可以上山爬树摘果、下水游泳捉蛇，但当听说有足球可踢时，他们都期待不已，传遍了山间的小伙伴。我们想众筹一支球队，在南太小学建立', 'img/project/shoutu/6.jpg', '21200', '800', '30', '2', '2016-01-12 00:00:00', '20', '0', '1', '1', '个人', null, '1');
INSERT INTO `project` VALUES ('7', '你是啥样的大米？一亩才产300斤', '优质珍稀米“小红稻”是安徽省无为县上世纪七十年代前种植的双季晚稻品种，具有千年历史。', 'img/project/shoutu/7.png', '50000', '800', '30', '1', '2016-03-15 00:00:00', '20', '0', '1', '1', '个人', null, '1');
INSERT INTO `project` VALUES ('8', '嗯！是甜过初恋的味道', '陕西延长县，优质苹果基地县，所产苹果个大、色艳、细脆、香甜，如今苹果丰收，却无商家上门，痛心。', 'img/project/shoutu/8.jpg', '10000', '800', '30', '1', '2016-05-15 00:00:00', '20', '0', '1', '1', '个人', null, '1');
