INSERT INTO `project` VALUES ('10', '《但见群山默》：用18个深山密林你闻所未闻的故事读懂火箭兵', '用400多页纸近30万字合成18个故事，让您读懂用青春和鲜血守卫国运和国魂的火箭兵，理解了他们，或许就更懂得我们的青春应当安放何处。', 'img/project/shoutu/10.jpg', '15000', '800', '30', '3', '2016-12-10 00:00:00', '10', '0', '1', '1', '个人', null, '1');
INSERT INTO `project` VALUES ('11', '“放学别走！”｜枕头大战约定你', '放下面具，释放压力，让心情与鹅毛一起飞舞，你准备好了么？', 'img/project/shoutu/11.jpg', '3800', '800', '30', '4', '2016-12-01 00:00:00', '10', '0', '1', '1', '个人', null, '1');
