INSERT INTO `project` VALUES ('14', '倾城之恋！抢救老洋房里的“上海滩”传奇', '花园洋房是上海最经典的住宅。作为中国首部老建筑纪录片，我们将走进上海老洋房，追忆歌舞升平、风华绝代的上海滩。希望能得到您的支持！', 'img/project/shoutu/14.jpg', '300000', '800', '30', '5', '2016-07-18 00:00:00', '60', '0', '1', '1', '个人', null, '1');
INSERT INTO `project` VALUES ('15', '皇上，臣在这儿', '北京作为一个拥有三千多年建城史和八百五十多年建都史的古老城市，其特有的皇家文化结合现代文化创作出不同感觉的纪念品。', 'img/project/shoutu/15.jpg', '66000', '800', '30', '5', '2016-02-16 00:00:00', '60', '0', '1', '1', '个人', null, '1');
INSERT INTO `project` VALUES ('16', '一个“农民”引发的史诗，乔家大院有话要说', '乔家大院是中国北方汉民族民居的典范。作为中国首部老建筑纪录片，我们将走进乔家大院，感受晋商文化。希望能得到您的支持！', 'img/project/shoutu/16.jpg', '300000', '800', '30', '5', '2016-06-15 00:00:00', '50', '0', '1', '1', '个人', null, '1');
